  and id = #{id,jdbcType=INTEGER}

mapper.delete:
delete from test_oxygen
where expid = #{expid,jdbcType=INTEGER}
  and id = #{id,jdbcType=INTEGER}

mapper.update:
update test_oxygen


    timestamp = #{timestamp,jdbcType=DOUBLE},


    vo2 = #{vo2,jdbcType=DOUBLE},


    vco2 = #{vco2,jdbcType=DOUBLE},


where expid = #{expid,jdbcType=INTEGER}
  and id = #{id,jdbcType=INTEGER}

update test_oxygen
set timestamp = #{timestamp,jdbcType=DOUBLE},
  vo2 = #{vo2,jdbcType=DOUBLE},
  vco2 = #{vco2,jdbcType=DOUBLE}
where expid = #{expid,jdbcType=INTEGER}
  and id = #{id,jdbcType=INTEGER}
