mapper.select:
select 

from tmp_foot_pressure_fgt
where expid = #{expid,jdbcType=INTEGER}
  and id = #{id,jdbcType=INTEGER}

mapper.delete:
delete from tmp_foot_pressure_fgt
where expid = #{expid,jdbcType=INTEGER}
  and id = #{id,jdbcType=INTEGER}

mapper.update:
update tmp_foot_pressure_fgt
set timestamp = #{timestamp,jdbcType=DOUBLE},
  left_x = #{left_x,jdbcType=DOUBLE},
  left_y = #{left_y,jdbcType=DOUBLE},
  right_x = #{right_x,jdbcType=DOUBLE},
  right_y = #{right_y,jdbcType=DOUBLE}
where expid = #{expid,jdbcType=INTEGER}
  and id = #{id,jdbcType=INTEGER}
